import styles from '../styles/Home.module.css'
import { useSession, signIn, signOut } from 'next-auth/react'
import { initFirebase } from '../firebaseApp'
import { getAuth, signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import { useAuthState } from "react-firebase-hooks/auth"
import { useRouter } from "next/router"
import { Image } from 'semantic-ui-react';
import { collection, doc, setDoc } from 'firebase/firestore'; 
import { db } from '../firebaseApp';
export default function Home() {
  const {data: session} = useSession()

  const app = initFirebase()

  const auth = getAuth();
  const provider = new GoogleAuthProvider(); 
  const [user, loading] = useAuthState(auth);
  const router = useRouter(); 

  const signIn = async () => {
    const result = await signInWithPopup(auth, provider); 
    console.log(result.user.uid)

  }

  
  if(loading) {
    return <p>Loading...</p>
  }

  if (user) { 
    router.push("/Dashboard")
    return <h1>Welcome, {user.displayName}</h1>
  }


  return (
    <div>
    <div className='header'>
    <h1 className='heading'>Welcome to FoodHub</h1>
    <small className='motto'>Where we give you the food and the drinks</small>
    <br />
    <div className='signin' onClick={signIn}>
    <Image className='google-icon' style={{ hieght: '100px' }} src="data:image/png;base64," alt="google-icons" />
    <span className='text'>Sign In</span>
    </div>
    </div>
    </div>
  )
}
